  //     });
  // };
  // getCaractersFromApi();

  const [isLoading, setLoading] = useState(true);
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch('https://rickandmortyapi.com/api/character')
      .then((response) => response.json())
      .then((json) => setData(json.results), console.log(json.results))
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  }, []);
  return (
    <View style={styles.container}>
    <Header
        center={<Text style={{ color: '#7CBC6C', fontSize: 20 }} >Rick & Morty</Text>}
      />
      <View style={styles.card}>
        <Image
          style={styles.image}
          source={require('./assets/images/rick-morty-season-4.jpg')}
        />
        <View style={styles.infos}>
          <Text style={styles.name}> Beth Smith</Text>
          <Text style={styles.details}> Alive: Human</Text>
          <Text style={styles.details}> Gendre: Female</Text>
          <Text style={styles.details}> Origin:  Earth (Evil Rick's Target Dimension)</Text>

        </View>
      </View>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'black',
    alignItems: 'center',
    // justifyContent: 'center',
  },
  card: {
    flexDirection:"row",
    marginTop: 150,
