import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';
import Header from './components/header';
export default function App() {
  const Circle = () => {
    return <View style={styles.circle} />;
  };
  StyleSheet.create({
    circle: {
      width: 100,
      height: 100,
      borderRadius: 100 / 2,
      backgroundColor: "red",
    },
  });
  return (
    <View style={styles.container}>
    <Header
        center={<Text style={{ color: '#7CBC6C', fontSize: 20 }} >Rick & Morty</Text>}
      />
      <View style={styles.card}>
        <Image
          style={styles.image}
          source={require('./assets/images/rick-morty-season-4.jpg')}
        />
        <View style={styles.infos}>
          <Text style={styles.name}> Beth Smith</Text>
          <Text style={styles.details}> Alive: Human</Text>
        </View>
      </View>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'black',
    alignItems: 'center',
    // justifyContent: 'center',
  },
  card: {
    flexDirection:"row",
    marginTop: 150,
    height: '20%',
    width: '80%',
    backgroundColor: '#7CBC6C',
    borderRadius: 10,
  },
  image:{
    flex:1,
    width:'40%',
    height:'100%',
    padding: 30,
  },
  infos:{
    flex:2,
    marginLeft:10,
    marginTop:10,
    flexDirection:'column'
  },
  name:{
    color:'white',
    fontSize: 20,
    fontWeight: 'bold',
  },
  details:{
    color:'white',
    marginTop:10,
    fontSize: 10
  }

});
