import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';
import Header from './components/header';
export default function App() {

  const getCaractersFromApi = () => {
    return fetch('https://rickandmortyapi.com/api/character')
      .then((response) => response.json())
      .then((json) => {
        console.log(json.caracters);
        return json.caracters;

      })
      .catch((error) => {
        console.error(error);
      });
  };
  getCaractersFromApi();
  return (
    <View style={styles.container}>
    <Header
        center={<Text style={{ color: '#7CBC6C', fontSize: 20 }} >Rick & Morty</Text>}
      />
      <View style={styles.card}>
        <Image
          style={styles.image}
          source={require('./assets/images/rick-morty-season-4.jpg')}
        />
        <View style={styles.infos}>
          <Text style={styles.name}> Beth Smith</Text>
          <Text style={styles.details}> Alive: Human</Text>
          <Text style={styles.details}> Gendre: Female</Text>
          <Text style={styles.details}> Origin:  Earth (Evil Rick's Target Dimension)</Text>

        </View>
      </View>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'black',
    alignItems: 'center',
    // justifyContent: 'center',
  },
  card: {
    flexDirection:"row",
    marginTop: 150,
    height: '20%',
    width: '80%',
    backgroundColor: '#7CBC6C',
    borderRadius: 10,
  },
  image:{
    flex:1,
    width:'40%',
    height:'100%',
    padding: 30,
    borderTopLeftRadius:10,
    borderBottomLeftRadius:10
  },
  infos:{
    flex:2,
    marginLeft:10,
    marginTop:10,
    flexDirection:'column'
  },
  name:{
    color:'white',
    fontSize: 20,
    fontWeight: 'bold',
  },
  details:{
    color:'white',
    marginTop:10,
    fontSize: 10
  }

});
