import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState } from 'react';
import { ActivityIndicator, FlatList, StyleSheet, Text, View, Image } from 'react-native';
import Header from './components/header';
export default function App() {


  const [isLoading, setLoading] = useState(true);
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch('https://rickandmortyapi.com/api/character')
      .then((response) => response.json())
      .then((json) => setData(json.results))
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  }, []);
  console.log(data)
  return (
    <View style={styles.container}>
    <Header
        center={<Text style={{ color: '#7CBC6C', fontSize: 20 }} >Rick & Morty</Text>}
      />
      {/* 
      {isLoading ? <ActivityIndicator/> : (
        <FlatList
          data={data}
          keyExtractor={({ id }, index) => id}
          renderItem={({ item }) => (
            <Text>{item.title}, {item.releaseYear}</Text>
          )}
        />
      )} */}
      {isLoading ? <ActivityIndicator/> : (
        <FlatList
          data={data}
          keyExtractor={({ id }, index) => id}
          renderItem={({ item }) => (
      <View style={styles.card}>
        <Image
          style={styles.image}
          source={{
            uri: item.image}}
        />
        <View style={styles.infos}>
          <Text style={styles.name}> Beth Smith</Text>
          <Text style={styles.details}> Alive: Human</Text>
          <Text style={styles.details}> Gendre: Female</Text>
          <Text style={styles.details}> Origin:  Earth (Evil Rick's Target Dimension)</Text>

        </View>
      </View>
      )}
        />
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'black',
    alignItems: 'center',
    // justifyContent: 'center',
  },
  card: {
    flexDirection:"row",
    marginTop: 150,
    height: 100,
    width: 300,
    backgroundColor: '#7CBC6C',
    borderRadius: 10,
  },
  image:{
    flex:1,
    padding: 30,
    borderTopLeftRadius:10,
    borderBottomLeftRadius:10
  },
  infos:{
    flex:2,
    marginLeft:10,
    marginTop:10,
    flexDirection:'column'
  },
  name:{
    color:'white',
    fontSize: 20,
    fontWeight: 'bold',
  },
  details:{
    color:'white',
    marginTop:10,
    fontSize: 10
  }

});
